                <script type="text/javascript">  
        jQuery(document).ready(function() {
            
         $.post('<?=  site_url('admin/get_activities/')?>'+'/'+jQuery('#event').val(),
                function(output1) {
                    $('#activities').html(output1).show();
                    $.post('<?=  site_url('admin/get_activity_year/')?>'+'/'+jQuery('#activities').val(),
                        function(output) {
                            $('#year').val(jQuery.trim(output));
                            files();
                    });
            });
           
         
        
         });
         jQuery('#event').change(function(){ 
        $.post('<?=  site_url('admin/get_activities/')?>'+'/'+jQuery('#event').val(),
                function(output1) {
                    $('#activities').html(output1).show();
            });
            $.post('<?=  site_url('admin/get_activity_year/')?>'+'/'+jQuery('#activities').val(),
                function(output2) {
                    $('#year').val(jQuery.trim(output2));
                    files();
        });  
        }); 
        jQuery('#activities').change(function(){ 
            // list files
		files();
	// list files ends
            $.post('<?=  site_url('admin/get_activity_year/')?>'+'/'+jQuery('#activities').val(),
                function(output4) {
                    $('#year').val(jQuery.trim(output4));
        });  
        });
        function files(){
             // list files
            var items = [];
                items.push('<li><p style="text-decoration:underline;color:green">Available Pictures In selected Activity</p></li>');
                var uploadURI2="<?=  site_url('admin/list_Files')?>"+'?year='+$('#year').val()+"&event="+$('#event').val()+"&activity="+$('#activities').val();
                 var docURI = "<?=  site_url('assets/activities')?>/";
		$.getJSON(uploadURI2, function(data) {
			$.each(data['files'], function(index, element) {
				items.push('<li class="list-group-item"><a target="_blank" href="' + docURI+data['dir']+element  + '">'+element+'</a><div class="pull-right"><a href="#" data-file="' + element + '" class="remove-file"><i class="icon-remove"></i></a></div></li>');
			});
			$('.list-group').html("").html(items.join(""));
		});
                // list files ends
        }
</script>
